import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Auth.css';

const Auth = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const navigate = useNavigate();

  const switchMode = () => {
    setIsSignUp((prevIsSignUp) => !prevIsSignUp);
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    const url = isSignUp 
      ? 'http://localhost:5000/api/auth/signup' 
      : 'http://localhost:5000/api/auth/login';

    try {
      const { data } = await axios.post(url, formData); // Use axios.post for request

      console.log('Response data:', data);
      if (data.message === 'Login successful' || data.message === 'Signup successful') {
        // Redirect or perform any other action after successful login/signup
        navigate('/home');
      } else {
        console.error(data.message);
      }
    } catch (error) {
      console.error('Error occurred:', error);
    }
  };

  return (
    <div className="auth-container">
      <div className='wrapper'>
        <div className='aa'>
      <img className='img' src='https://th.bing.com/th/id/OIP.cnjDHo5kR07KF4fvchOCnAHaE7?rs=1&pid=ImgDetMain' alt='img'></img>
      <div className={`auth-form ${isSignUp ? 'show' : ''}`}>
        <h2>{isSignUp ? 'Sign Up' : 'Login'}</h2>
        <form onSubmit={handleSubmit}>
          {isSignUp && (
            <>
              <input
                type="text"
                name="firstName"
                placeholder="First Name"
                value={formData.firstName}
                onChange={handleChange}
                required
              />
              <input
                type="text"
                name="lastName"
                placeholder="Last Name"
                value={formData.lastName}
                onChange={handleChange}
                required
              />
            </>
          )}
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          {isSignUp && (
            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirm Password"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
          )}
          <button type="submit">{isSignUp ? 'Sign Up' : 'Login'}</button>
        </form>
        <p onClick={switchMode}>
          {isSignUp
            ? 'Already have an account? Login here'
            : "Don't have an account? Sign Up here"}
        </p>
      </div>
      </div>
      </div>
    </div>
  );
};

export default Auth;
