import React, { useState, useEffect } from 'react';
import './profile.css'; // Import CSS file for styling
import axios from 'axios'; // To handle image upload requests

const Profile = ({ user, setUser }) => {
  const [imageFile, setImageFile] = useState(null); // State to store selected image preview
  const defaultProfileImage = 'https://www.pphfoundation.ca/wp-content/uploads/2018/05/default-avatar.png';

  // Function to handle image selection and upload
  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(URL.createObjectURL(file)); // Display image preview before upload

      const formData = new FormData();
      formData.append('profileImage', file); // 'profileImage' should match your backend field
      formData.append('userId', user._id); // Assuming the user's ID is available in the user object

      try {
        // Send POST request to upload the image
        const response = await axios.post('http://localhost:5000/api/user/uploadProfileImage', formData, {
          headers: {
            'Content-Type': 'multipart/form-data',
          },
        });

        // Update the user profile image in the state after successful upload
        setUser({ ...user, profileImage: response.data.profileImage });
      } catch (error) {
        console.error('Image upload failed:', error);
      }
    }
  };

  return (
    <div className="profile-container">
      <h1 className="profile-title">User Profile</h1>
      {user ? (
        <div className="profile-info">
          <div className="profile-details">
            <p className="profile-name">Name: {user.firstName} {user.lastName}</p>
            <p className="profile-email">Email: {user.email}</p>
          </div>
          <div className="profile-image1-section">
            <img
              src={user.profileImage || imageFile || defaultProfileImage}
              alt="Profile"
              className="profile-image1"
            />
            <div className="edit-overlay">
              <label htmlFor="image-upload" className="edit-button">
                Edit Profile
              </label>
              <input
                type="file"
                id="image-upload"
                accept="image/*"
                onChange={handleImageChange}
                className="file-input"
              />
            </div>
          </div>
        </div>
      ) : (
        <p className="profile-login-message">Please log in to see your profile.</p>
      )}
    </div>
  );
};

export default Profile;
