import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Ensure you're using react-router-dom
import axios from 'axios';
import './Auth.css'; // Assuming you have this file for styling

const Auth = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const history = useNavigate(); // React Router hook for navigation

  // Switch between Signup and Login modes
  const switchMode = () => {
    setIsSignUp((prevIsSignUp) => !prevIsSignUp);
  };

  // Handle form field changes
  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  // Handle form submission
  const handleSubmit = async (e) => {
    e.preventDefault();

    const url = isSignUp
      ? 'http://localhost:5000/api/auth/signup'
      : 'http://localhost:5000/api/auth/login';

    // If it's a signup, send the full form data, otherwise just email and password
    const data = isSignUp
      ? {
          firstName: formData.firstName,
          lastName: formData.lastName,
          email: formData.email,
          password: formData.password,
        }
      : { email: formData.email, password: formData.password };

    try {
      const response = await axios.post(url, data);

      if (isSignUp) {
        if (response.data === 'exist') {
          alert('User already exists');
        } else if (response.data === 'notexist') {
          alert('Signup successful!');
          history('/hotel', { state: { id: formData.email } });
        }
      } else {
        if (response.data === 'exist') {
          alert('Login successful!');
          history('/hotel', { state: { id: formData.email } });
        } else if (response.data === 'notexist') {
          alert('User has not signed up');
        }
      }
    } catch (error) {
      alert('An error occurred. Please try again.');
      console.error('Error:', error);
    }
  };

  return (
    <div className="auth-container">
      <div className={`auth-form ${isSignUp ? 'show' : ''}`}>
        <h2>{isSignUp ? 'Sign Up' : 'Login'}</h2>
        <form onSubmit={handleSubmit}>
          {/* Signup fields */}
          {isSignUp && (
            <>
              <input
                type="text"
                name="firstName"
                placeholder="First Name"
                value={formData.firstName}
                onChange={handleChange}
                required
              />
              <input
                type="text"
                name="lastName"
                placeholder="Last Name"
                value={formData.lastName}
                onChange={handleChange}
                required
              />
            </>
          )}
          {/* Shared fields for both login and signup */}
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={formData.email}
            onChange={handleChange}
            required
          />
          <input
            type="password"
            name="password"
            placeholder="Password"
            value={formData.password}
            onChange={handleChange}
            required
          />
          {/* Confirm password for signup */}
          {isSignUp && (
            <input
              type="password"
              name="confirmPassword"
              placeholder="Confirm Password"
              value={formData.confirmPassword}
              onChange={handleChange}
              required
            />
          )}
          <button type="submit">{isSignUp ? 'Sign Up' : 'Login'}</button>
        </form>
        <p onClick={switchMode}>
          {isSignUp
            ? 'Already have an account? Login here'
            : "Don't have an account? Sign Up here"}
        </p>
      </div>
    </div>
  );
};

export default Auth;
