import React, { useState } from 'react';
import './profile.css'; // Import CSS file for styling
import axios from 'axios'; // To handle image upload requests

const Profile = ({ user, setUser }) => {
  const [formData, setFormData] = useState({
    firstName: user?.firstName || '',
    email: user?.email || '',
    password: '', // Empty initially
  });
  const [imageFile, setImageFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const [editing, setEditing] = useState(false); // To toggle edit mode
  const defaultProfileImage = 'https://www.pphfoundation.ca/wp-content/uploads/2018/05/default-avatar.png';

  const token = localStorage.getItem("token"); 
  if (!token) {
    return <p>Please log in to see your profile.</p>;
  }

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleImageChange = async (e) => {
    const file = e.target.files[0];
    if (file) {
      setImageFile(URL.createObjectURL(file));
      setLoading(true);

      const formData = new FormData();
      formData.append("profileImage", file);
      formData.append("userId", user?._id); // Use optional chaining

      try {
        const response = await axios.post("http://localhost:5000/api/user/uploadProfileImage", formData, {
          headers: {
            "Content-Type": "multipart/form-data",
            "Authorization": `Bearer ${token}`,
          },
        });

        setUser({ ...user, profileImage: response.data.profileImage });
      } catch (error) {
        console.error("Image upload failed:", error);
      } finally {
        setLoading(false);
      }
    }
  };

  const handleFormSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    try {
      const response = await axios.put(
        `http://localhost:5000/api/user/update`,
        { ...formData, userId: user._id },
        {
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${token}`,
          },
        }
      );

      setUser(response.data); // Update user info with new data
      setEditing(false); // Switch back to view mode
    } catch (error) {
      console.error("Error updating profile:", error);
    } finally {
      setLoading(false);
    }
  };

  const profileImageUrl = user?.profileImage ? `http://localhost:5000/${user.profileImage}` : imageFile || defaultProfileImage;

  return (
    <div className="profile-container">
      <h1 className="profile-title">User Profile</h1>
      <div className="profile-content">
        {/* Profile Edit Section */}
        <div className="profile-edit-section">
          {editing ? (
            <form onSubmit={handleFormSubmit} className="profile-edit-form">
              <div className="form-group">
                <label htmlFor="firstName">Name</label>
                <input
                  type="text"
                  id="firstName"
                  name="firstName"
                  value={formData.firstName}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="email">Email</label>
                <input
                  type="email"
                  id="email"
                  name="email"
                  value={formData.email}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="password">Password</label>
                <input
                  type="password"
                  id="password"
                  name="password"
                  value={formData.password}
                  onChange={handleInputChange}
                  placeholder="Enter new password"
                />
              </div>
              <button type="submit" className="submit-btn" disabled={loading}>
                {loading ? 'Updating...' : 'Update Profile'}
              </button>
            </form>
          ) : (
            <div className="profile-details">
              <p className="profile-name">Name: {user.firstName}</p>
              <p className="profile-email">Email: {user.email}</p>
              <button onClick={() => setEditing(true)} className="edit-profile-btn">Edit Profile</button>
            </div>
          )}
        </div>

        {/* Profile Image Section */}
        <div className="profile-image1-section">
          <img
            src={profileImageUrl}
            alt="Profile"
            className="profile-image1"
          />
          <div className="edit-overlay">
            <label htmlFor="image-upload" className="edit-button">
              Change Image
            </label>
            <input
              type="file"
              id="image-upload"
              accept="image/*"
              onChange={handleImageChange}
              className="file-input"
            />
          </div>
        </div>
      </div>

      {loading && <p>Processing...</p>} {/* Show loading message */}
    </div>
  );
};

export default Profile;
