import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import './Auth.css';

const Auth = () => {
  const [isSignUp, setIsSignUp] = useState(false);
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    confirmPassword: '',
  });

  const navigate = useNavigate();

  const switchMode = (e) => {
    e.preventDefault(); // Prevent any default behavior (such as form submission)
    setIsSignUp((prevIsSignUp) => !prevIsSignUp);
    setFormData({
      firstName: '',
      lastName: '',
      email: '',
      password: '',
      confirmPassword: '',
    }); // Clear form data on switch
  };

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    const url = isSignUp 
      ? 'http://localhost:5000/api/auth/signup' 
      : 'http://localhost:5000/api/auth/login';

    try {
      const { data } = await axios.post(url, formData);

      console.log('Response data:', data);
      if (data.message === 'Login successful' || data.message === 'Signup successful') {
        navigate('/home');
      } else {
        console.error(data.message);
      }
    } catch (error) {
      console.error('Error occurred:', error);
    }
  };

  return (
    <div className="auth-container">
      <div className="wrapper">
        <div className={`aa ${isSignUp ? 'move-left' : ''}`}>
          <img 
            className={`img ${isSignUp ? 'move-right' : ''}`} 
            src="https://imbuecreative.com/wp-content/uploads/2021/03/Facebook-trends-of-2021-bl.png" 
            alt="img"
          />
          <div className={`auth-form ${isSignUp ? 'show-signup' : 'show-login'}`}>
            <h2>{isSignUp ? 'Sign Up' : 'Login'}</h2>
            <form onSubmit={handleSubmit}>
              {isSignUp && (
                <>
                  <input
                    type="text"
                    name="firstName"
                    placeholder="First Name"
                    value={formData.firstName}
                    onChange={handleChange}
                    required
                  />
                  <input
                    type="text"
                    name="lastName"
                    placeholder="Last Name"
                    value={formData.lastName}
                    onChange={handleChange}
                    required
                  />
                </>
              )}
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
              <input
                type="password"
                name="password"
                placeholder="Password"
                value={formData.password}
                onChange={handleChange}
                required
              />
              {isSignUp && (
                <input
                  type="password"
                  name="confirmPassword"
                  placeholder="Confirm Password"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  required
                />
              )}
              <button type="submit">{isSignUp ? 'Sign Up' : 'Login'}</button>
            </form>
            <p onClick={switchMode}>
              {isSignUp
                ? 'Already have an account? Login here'
                : "Don't have an account? Sign Up here"}
            </p>
          </div>
        </div>
        <button className="arrow-button" onClick={switchMode}>
          âž”
        </button>
      </div>
    </div>
  );
};

export default Auth;
