import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import './Navbar.css';

const Navbar = () => {
  const [user, setUser] = useState(null);
  const defaultProfileImage = 'https://www.pphfoundation.ca/wp-content/uploads/2018/05/default-avatar.png'; 

  useEffect(() => {
    const fetchUserProfile = async () => {
      const token = localStorage.getItem('token');  // Assuming you store the token in localStorage

      if (!token) return; // If no token, do not fetch user profile

      try {
        const response = await fetch('/api/user/profile', {
          headers: {
            'Authorization': `Bearer ${token}`
          }
        });

        if (!response.ok) {
          throw new Error('Failed to fetch user profile');
        }

        const data = await response.json();
        setUser(data); // Update user state with fetched data
        console.log(data);
      } catch (error) {
        console.error('Error fetching user profile:', error);
      }
    };

    fetchUserProfile();
  }, []); // Run this only once on mount

  return (
    <nav className="navbar">
      <div className="navbar-logo">
        <Link to="/">Resume & Portfolio Builder</Link>
      </div>
      <ul className="navbar-links">
        <li><Link to="/">Home</Link></li>
        <li><Link to="/about">About</Link></li>
        <li><Link to="/features">Features</Link></li>
        <li><Link to="/contact">Contact</Link></li>
      </ul>
      <div className="navbar-cta">
        {user ? (
          <Link to="/profile" className="profile-icon">
            <img 
              src={user.profileImage || defaultProfileImage} 
              alt="Profile" 
              className="profile-image" 
            />
          </Link>
        ) : (
          <Link to="/login" className="cta-button">
            Get Started
          </Link>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
